import React, { useContext, useReducer } from "react";
import axios from "axios";
import GithubContext from "./githubContext";
import GithubReducer from "./githubReducer";
import {
  SEARCH_USERS,
  SET_LOADING,
  CLEAR_USERS,
  GET_USER,
  GET_REPOS,
  SET_ERROR_MESSAGE,
  FETCH_FIRST_30_USERS,
} from "../types";

const GithubState = (props) => {
  const initialState = {
    users: [],
    user: {},
    repos: [],
    loading: false,
    errorMessage: "",
  };

  const [state, dispatch] = useReducer(GithubReducer, initialState);
  const githubContext = useContext(GithubContext);

  // Get first 30 users
  const fetchFirst30Users = async () => {
    setLoading();
    try {
      const response = await axios.get(
        `https://api.github.com/users?client_id=${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`
      );
      console.log(response.data);
      dispatch({
        type: FETCH_FIRST_30_USERS,
        payload: response.data,
      });
    } catch (error) {
      console.log(error);
      dispatch({
        type: SET_ERROR_MESSAGE,
        payload: error.message,
      });
    }
  };

  // Search Users
  const searchUsers = async (text) => {
    setLoading();
    try {
      const response = await axios.get(
        `https://api.github.com/search/users?q=${text}&client_id=${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`
      );
      console.log(response.data);
      dispatch({
        type: SEARCH_USERS,
        payload: response.data.items,
      });
    } catch (error) {
      console.log(error.message);
    }
  };

  // Clear users from state
  const clearUsers = () => {
    dispatch({ type: CLEAR_USERS });
  };

  //Conditionally render the clear users button
  const showOrHideClearButton = () => {
    return githubContext.users.length > 0 ? true : false;
  };

  //Get Repos

  //Set Loading
  const setLoading = () => {
    dispatch({ type: SET_LOADING });
  };

  return (
    <GithubContext.Provider
      value={{
        users: state.users,
        user: state.user,
        repos: state.repos,
        loading: state.loading,
        errorMessage: state.errorMessage,
        searchUsers,
        fetchFirst30Users,
        clearUsers,
        showOrHideClearButton,
      }}
    >
      {props.children}
    </GithubContext.Provider>
  );
};

export default GithubState;
