import React, { Component, Fragment } from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Navbar from "./components/layout/Navbar";
import Users from "./components/users/Users";
import axios from "axios";
import "./App.css";
import Footer from "./components/layout/Footer";
import Search from "./components/users/Search";
import Alert from "./components/layout/Alert";
import About from "./components/pages/About";
import User from "./components/users/User";

class App extends Component {
  state = {
    users: [],
    user: {},
    loading: false,
    errorMessage: "",
    alert: null,
  };

  //display first 30 github users
  async componentDidMount() {
    this.setState({ loading: true });

    try {
      const response = await axios.get(
        `https://api.github.com/users?client_id=${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`
      );
      console.log(response.data);

      this.setState({ users: response.data, loading: false });
    } catch (error) {
      console.log(error);
      this.setState({ errorMessage: error.message, loading: false });
    }
  }

  //Search Github users
  searchUsers = async (text) => {
    this.setState({ loading: true });
    try {
      const response = await axios.get(
        `https://api.github.com/search/users?q=${text}&client_id=${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`
      );
      console.log(response.data);
      this.setState({ users: response.data.items, loading: false });
      /* if (response.data.items === 0) {
        throw new SyntaxError(
          "Sorry, but your search did not return any result"
        );
      } else {
        this.setState({ users: response.data.items, loading: false });
      } */
    } catch (error) {
      console.log(error.message);
    }
  };

  // Get single user
  getUser = async (username) => {
    this.setState({ loading: true });

    try {
      const response = await axios.get(
        `https://api.github.com/users/${username}?client_id=${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`
      );
      console.log(response.data);

      this.setState({ user: response.data, loading: false });
    } catch (error) {
      console.log(error);
    }
  };

  // Clear users from state
  clearUsers = () => {
    this.setState({ users: [], loading: false });
  };

  //Show search validation alert message
  showAlert = (message, type) => {
    this.setState({ alert: { message: message, type: type } });

    //Make the alert go away after 6sec
    setTimeout(() => {
      this.setState({ alert: null });
    }, 6000);
  };

  //Conditionally render the clear users button
  showOrHideClearButton = () => {
    return this.state.users.length > 0 ? true : false;
  };

  render() {
    const { users, user, loading, errorMessage } = this.state;

    return (
      <Router>
        <div className='App'>
          <Navbar />
          <div className='container'>
            <Alert alert={this.state.alert} />
            <Switch>
              <Route
                exact
                path='/'
                render={(props) => (
                  <Fragment>
                    <Search
                      searchUsers={this.searchUsers}
                      clearUsers={this.clearUsers}
                      showOrHideClearButton={this.showOrHideClearButton()}
                      showAlert={this.showAlert}
                    />
                    <Users
                      loading={loading}
                      users={users}
                      errorMessage={errorMessage}
                    />
                  </Fragment>
                )}
              />
              <Route exact path='/about' component={About} />
              <Route
                exact
                path='/user/:login'
                render={(props) => (
                  <User
                    getUser={this.getUser}
                    loading={loading}
                    {...props}
                    user={user}
                  />
                )}
              />
            </Switch>
          </div>
          <div>
            <Footer />
          </div>
        </div>
      </Router>
    );
  }
}

export default App;
