import React, { Component } from "react";
import Navbar from "./components/layout/Navbar";
import Users from "./components/users/Users";
import axios from "axios";
import "./App.css";
import Footer from "./components/layout/Footer";
import Search from "./components/users/Search";
import Alert from "./components/layout/Alert";

class App extends Component {
  state = {
    users: [],
    loading: false,
    errorMessage: "",
    alert: null,
  };

  //display first 30 github users
  async componentDidMount() {
    this.setState({ loading: true });

    const response = await axios.get(
      `https://api.github.com/users?client_id=${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`
    );
    console.log(response.data);

    this.setState({ users: response.data, loading: false });
  }

  //Search Github users
  searchUsers = async (text) => {
    this.setState({ loading: true });

    const response = await axios.get(
      `https://api.github.com/search/users?q=${text}&client_id=${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`
    );
    console.log(response.data);

    this.setState({ users: response.data.items, loading: false });
    // console.log(text);
  };

  // Clear users from state
  clearUsers = () => {
    this.setState({ users: [], loading: false });
  };

  //Show search validation alert message
  showAlert = (message, type) => {
    this.setState({ alert: { message: message, type: type } });

    //Make the alert go away after 6sec
    setTimeout(() => {
      this.setState({ alert: null });
    }, 6000);
  };

  //Conditionally render the clear users button
  showOrHideClearButton = () => {
    return this.state.users.length > 0 ? true : false;
  };

  render() {
    const { users, loading } = this.state;
    return (
      <div className='App'>
        <Navbar />
        <div className='container'>
          <Alert alert={this.state.alert} />
          <Search
            searchUsers={this.searchUsers}
            clearUsers={this.clearUsers}
            showOrHideClearButton={this.showOrHideClearButton()}
            showAlert={this.showAlert}
          />
          <Users loading={loading} users={users} />
        </div>
        <div>
          <Footer />
        </div>
      </div>
    );
  }
}

export default App;
