import {
  FETCH_FIRST_30_USERS,
  SEARCH_USERS,
  SET_ERROR_MESSAGE,
  SET_LOADING,
} from "../types";

// eslint-disable-next-line
export default (state, action) => {
  switch (action.type) {
    case FETCH_FIRST_30_USERS:
      return {
        ...state,
        users: action.payload,
        loading: false,
        errorMessage: "",
      };
    case SEARCH_USERS:
      return {
        ...state,
        users: action.payload,
        loading: false,
        errorMessage: "",
      };
    case SET_LOADING:
      return {
        ...state,
        loading: true,
      };
    case SET_ERROR_MESSAGE:
      return {
        ...state,
        loading: false,
        users: [],
        error: action.payload,
      };
    default:
      return state;
  }
};
