import React, { Component } from "react";

class UserItem extends Component {
  constructor() {
    super();
    this.state = {
      id: "id",
      login: "mojombo",
      avatar_url: "https://avatars0.githubusercontent.com/u/1?v=4",
      html_url: "https://github/mojombo",
    };
  }

  render() {
    const userItemStyles = {
      margin: "20px 5px",
    };
    const imageStyles = {
      width: "60px",
    };

    return (
      <div className='card text-center'>
        <img
          src={this.state.avatar_url}
          alt='userImage'
          className='round-img'
          style={imageStyles}
        />
        <h3>{this.state.login}</h3>
        <div>
          <a href={this.state.html_url} className='btn ntndark btn-sm my-1'>
            More
          </a>
        </div>
      </div>

      /*
      <div style={userItemStyles}>
        <div>User Items</div>
      </div>*/
    );
  }
}

export default UserItem;
