import React, { Component } from "react";
import "./App.css";
class App extends Component {
  render() {
    const user = {
      name: "Emmanuel Peter",
      firstTermScore: 40,
      secondTermScore: 80,
      thirdTermscore: 95,
    };

    const computeGPA = () => {
      Math.round(
        (user.firstTermScore + user.secondTermScore + user.thirdTermscore) / 3
      );
    };

    return (
      <React.Fragment>
        <h1>Hello {user.name.toUpperCase()}</h1>
        <h3 className='text-info'>Your GPA is: {computeGPA()}</h3>
        <h3 className='text-success'>
          This is an introduction to the React Framework
        </h3>
      </React.Fragment>
    );
  }
}

export default App;
