import React, { Fragment, useState, useEffect } from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Navbar from "./components/layout/Navbar";
import Users from "./components/users/Users";
import axios from "axios";
import "./App.css";
import Footer from "./components/layout/Footer";
import Search from "./components/users/Search";
import Alert from "./components/layout/Alert";
import About from "./components/pages/About";
import User from "./components/users/User";
import NotFound from "./components/pages/NotFound";
import GithubState from "./components/context/github/GithubState";
// import GithubContext from "./components/context/github/githubContext";

const App = () => {
  const [user, setUser] = useState({});
  const [repos, setRepos] = useState([]);
  const [loading, setLoading] = useState(false);
  const [alert, setAlert] = useState(null);

  //display first 30 github users

  // Get a user
  const getUser = async (username) => {
    setLoading(true);

    try {
      const response = await axios.get(
        `https://api.github.com/users/${username}?client_id=${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`
      );
      console.log(response.data);

      setUser(response.data);
      setLoading(false);
    } catch (error) {
      console.log(error);
    }
  };

  //Get users repos
  const getUserRepos = async (username) => {
    setLoading(true);

    try {
      const response = await axios.get(
        `https://api.github.com/users/${username}/repos?per_page=10&sort=created:asc&client_id=${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`
      );
      console.log(response.data);

      setRepos(response.data);
      setLoading(false);
    } catch (error) {
      console.log(error);
    }
  };

  //Show search validation alert message
  const showAlert = (message, type) => {
    setAlert({ message, type });

    //Make the alert go away after 6sec
    setTimeout(() => {
      setAlert(null);
    }, 6000);
  };

  return (
    <GithubState>
      <Router>
        <div className='App'>
          <Navbar />
          <div className='container'>
            <Alert alert={alert} />
            <Switch>
              <Route
                exact
                path='/'
                render={(props) => (
                  <Fragment>
                    <Search showAlert={showAlert} />
                    <Users />
                  </Fragment>
                )}
              />
              <Route exact path='/about' component={About} />
              <Route
                exact
                path='/user/:login'
                render={(props) => (
                  <User
                    {...props}
                    getUser={getUser}
                    getUserRepos={getUserRepos}
                    repos={repos}
                    loading={loading}
                    user={user}
                  />
                )}
              />
              <Route exact path='*' component={NotFound} />
            </Switch>
          </div>
          <div>
            <Footer />
          </div>
        </div>
      </Router>
    </GithubState>
  );
};

export default App;
