import React from "react";

const Footer = () => {
  return (
    <div style={FooterStyle}>
      <p>
        &copy; 2021 Github Users Finder App
        <br /> Designed by{" "}
        <a
          style={link}
          href='https://noble-247.github.io/Emmanuel/'
          target='_blank'
          rel='noopener noreferrer'
        >
          Emmanuel
        </a>
      </p>
    </div>
  );
};

const FooterStyle = {
  color: "white",
  backgroundColor: "#dc3545",
  padding: "10px",
  textAlign: "center",
  lineHeight: "1.5",
};

const link = {
  color: "white",
  hover: {
    color: "black",
  },
};
export default Footer;
